import './App.css';
import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { ListGroup, ListGroupItem, Button, Jumbotron, Container, Row, Col } from 'react-bootstrap';
import employee from './employees.json';

class App extends React.Component {
 state = {
   display: "home"
 }

 render() {
   return (
    <div className="App">
      {this.state.display === 'home' && (
    <Container>
      <Row>
        <Col className="jumboTron">
          <Jumbotron fluid>
              <h1>Welcome to your Employee Directory!</h1>

              <hr/>

              <p>Select from the list below to select what you would like to do.</p>


          </Jumbotron>
        </Col>       

      </Row>
      <Row>

        <Col className="sideLinks">
          <div className="employeeList">
              <ListGroup>
                  <ListGroupItem><Button href="departments" variant="outline-primary" block>View Departments</Button></ListGroupItem>
                  <ListGroupItem><Button href="sort" variant="outline-primary" block>Sort</Button></ListGroupItem>
                  <ListGroupItem><Button href="search" variant="outline-primary" block>Search Employees</Button></ListGroupItem>
                  <ListGroupItem><Button href="add" variant="outline-primary" block>Add Employees</Button></ListGroupItem>
                  <ListGroupItem><Button href="remove" variant="outline-primary" block>Remove Employees</Button></ListGroupItem>
              </ListGroup>
          </div>
        </Col>
      </Row>
    </Container>
      )}
      {this.state.display === 'departments'}
  </div>
   )
 }
}


export default App;
