import React from 'react';
import { Table, Card, ListGroup } from 'react-bootstrap';
import employees from '../employees.json'

class EmployeeTable extends React.Component {
    state= {
        employees: employees
    }

    componentDidMount() {
        const nextState = [];

        console.log(this.state.employees)
        this.state.employees[0].results.map((emp) => {
            return {
                firstname: emp.name.first,
                lastname: emp.name.last,
                phone: emp.cell,
                gender: emp.gender,
                email: emp.email,
                picture: emp.picture.thumbnail,
                visible: true
            }
        })

    }
    render() {
        return(
            this.state.employees.map((employee) => 
        <div>
            <Card className="employeeCard" style={{ width: "100%"}}>
                <Card.Body>
                    <Card.Title>{employee.firstname} {employee.lastname}</Card.Title>
                    <Card.Text>
                        <ListGroup.Item>Department: {employee.department}</ListGroup.Item>
                        <ListGroup.Item>Email: {employee.email}</ListGroup.Item>
                    </Card.Text>                
                </Card.Body>
            </Card>
        </div>)

        )
    }
}

export default EmployeeTable
